.article-entry h1 {
  font-size: 2em;
}
.article-entry h2 {
  font-size: 1.5em;
  position: relative;
}
.article-entry h2:before {
  content: "";
  position: absolute;
  width: 100%;
  border-bottom: 1px solid var(--color-h2-border);
  bottom: -15px;
  box-sizing: unset;
  transition: 1s;
}
.article-entry h2:after {
  content: "";
  position: absolute;
  background: var(--color-h2-after);
  width: 1em;
  height: 7px;
  bottom: -18px;
  left: 0;
  border-radius: 10px;
  box-shadow: 0 2px 12px var(--color-red-6-shadow);
  transition: 0.3s;
}
.article-entry h2:hover:after {
  width: 3em;
}
.article-entry h3 {
  font-size: 1.3em;
}
.article-entry h4 {
  font-size: 1.2em;
}
.article-entry h5 {
  font-size: 1em;
}
.article-entry h6 {
  font-size: 1em;
  color: var(--grey-9);
}
.article-entry hr {
  margin: 10px 0;
  opacity: 0.3;
  height: 4px;
  background-image: linear-gradient(to right, var(--red-0) 50%, transparent 0%);
  background-size: 50px 4px;
  background-repeat: repeat-x;
  border: none;
  filter: drop-shadow(0px 0px 2px var(--red-1));
  transition: opacity 0.3s, color 0.3s;
}
.article-entry hr:hover {
  opacity: 0.5;
}
.article-entry strong {
  font-weight: bold;
}
.article-entry em,
.article-entry cite {
  font-style: italic;
}
.article-entry sup,
.article-entry sub {
  font-size: 0.75em;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}
.article-entry sup {
  top: -0.5em;
}
.article-entry sub {
  bottom: -0.2em;
}
.article-entry small {
  font-size: 0.85em;
}
.article-entry acronym,
.article-entry abbr {
  text-decoration: none;
  border-bottom: 1px dotted;
}
.article-entry ul,
.article-entry ol,
.article-entry dl {
  margin: 0 20px;
  line-height: 1.8em;
}
.article-entry ul ul,
.article-entry ol ul,
.article-entry ul ol,
.article-entry ol ol {
  margin-top: 0;
  margin-bottom: 0;
}
.article-entry ul {
  list-style: disc;
}
.article-entry ol {
  list-style: decimal;
}
.article-entry dt {
  font-weight: bold;
}
